<template>
  <div class="container share">
    <!-- 换一张按钮 -->
    <div @click="shareChange" class="share_change" v-show="changeShow">换一张</div>
    <!-- 背景列表 -->
    <div class="mark" :class="markShow?'':'mark_position_left'">
      <p>--请选择生成图片--</p>
      <div class="mark_item" v-for="(item,index) in imgUrls" :key="index" @click="getBgImg(item)">
        <img :src="item.imgurl" alt="">
      </div>
    </div>
    <!-- 生成图片的DOM -->
    <div class="share_box" ref="box">
      <h4 style="color: red">我是文字</h4>
      <div class="img-box">
        <!-- 背景图 -->
        <img class="shareBg" @load="bgImgload" :src="bgImg" alt="">
      </div>
    </div>
    <!-- 存放合成的图片 -->
    <div class="share-img" id="shareImg"></div>
  </div>
</template>

<script>
import html2canvas from 'html2canvas'
import Canvas2Image from 'canvas2image'
export default {
  data () {
    return {
      changeShow: false,
      markShow: true,
      qrCode: true,
      pid: 0,
      imgUrls: [
        {pid: 1, imgurl: './static/images/active_peo3.png', isClass: 'shar1'},
        {pid: 2, imgurl: './static/images/active_peo4.png', isClass: 'shar2'},
        {pid: 3, imgurl: './static/images/active_peo5.png', isClass: 'shar3'},
        {pid: 4, imgurl: './static/images/active_peo6.png', isClass: 'shar4'}
      ],
      bgImg: '',
      qrCodeUrl: 'data:image/jpeg;base64,',
      invitationCode: '',
      timer: null
    }
  },
  created () {
    // 邀请码
    // this.invitationCode = this.$route.query.invitationCode
    // // 获取二维码
    // this.getQrCodeBase64()
  },
  mounted () {},

  methods: {
    // 换一张
    shareChange () {
      this.changeShow = !this.changeShow
      document.getElementById('shareImg').innerHTML = ''
      this.markShow = true
    },
    // 获取二维码
    getQrCodeBase64 () {
      let that = this
      that.$http.get('user/getInviteQrCode', {
        parmas: {
          code: that.invitationCode
        }
      }).then(res => {
        if (res && res.data.code === 0) {
          that.qrCodeUrl = res.data.data
        }
      })
    },
    // 获取背景图
    getBgImg (e) {
      let that = this
      that.changeShow = true
      that.markShow = false
      if (e.pid === that.pid) {
        // 前一张加载过
        that.bgImgload()
      } else {
        // 未加载过
        that.pid = e.pid
        that.bgImg = e.imgurl
      }
    },
    // 二维码是否渲染完毕
    qrcodeLoad () {
      this.qrCode = true
    },
    // 背景图加载完回调
    bgImgload () {
      // this.$indicator.open({
      //   text: '正在生成图片...',
      //   spinnerType: 'fading-circle'
      // })
      this.creatImg()
    },
    // 生成图片 ----  将box对应的dom转化为图片，可以做截屏或者其他使用
    // dom转化为canvas，再将canvas转化为图片
    // https://juejin.im/post/5df2e8ab6fb9a0163770816d?utm_source=gold_browser_extension
    // Canvas2Image.convertToPNG(canvas) 方法报错时，在依赖包中canvas2image.js， 最后一行添加 export default Canvas2Image;
    creatImg () {
      let that = this
      if (that.qrCode) {
        // 已经生成二维码
        clearInterval(this.timer)
        let element = that.$refs.box
        html2canvas(element, {useCORS: true, allowTaint: true, taintTest: false}).then(function (canvas) {
          document.getElementById('shareImg').appendChild(Canvas2Image.convertToPNG(canvas))
          alert('图片已生成，长按保存分享给你的好友吧')
          // that.$indicator.close()
          // that.$toast({
          //   message: '图片已生成，长按保存分享给你的好友吧',
          //   position: 'middle',
          //   duration: 2000
          // })
        })
      } else {
        that.timer = setInterval(() => {
          that.creatImg()
        }, 300)
      }
    }
  },
  destroyed () {
    // this.$indicator.close()
    clearInterval(this.timer)
  }
}
</script>

